import React, { useEffect } from 'react';
import Swiper from 'swiper';
import 'swiper/swiper-bundle.css';
import '../styles/Swiper.css';

const Swipe = () => {
  useEffect(() => {
    const swiper = new Swiper(".cubeSwiper", {
      effect: "cube",
      grabCursor: true,
      pauseOnMouseEnter: true,
      speed: 2000,
      autoplay: {
        delay: 2500,
        disableOnInteraction: false,
      },
      cubeEffect: {
        shadow: false,
        slideShadows: true,
        shadowOffset: 20,
        shadowScale: 0.94,
      },
      pagination: {
        el: ".swiper-pagination",
      },
    });

    // Cleanup Swiper instance when component unmounts
    return () => {
      swiper.destroy();
    };
  }, []); // Empty dependency array means this effect will run once on mount

  return (
    <figure className="cube-parent-container">
      <div className="swiper cubeSwiper">
        <div className="swiper-wrapper">
          <div className="swiper-slide">
            <img src="https://swiperjs.com/demos/images/nature-1.jpg" alt="Slide 1" />
          </div>
          <div className="swiper-slide">
            <img src="https://swiperjs.com/demos/images/nature-2.jpg" alt="Slide 2" />
          </div>
          <div className="swiper-slide">
            <img src="https://swiperjs.com/demos/images/nature-3.jpg" alt="Slide 3" />
          </div>
          <div className="swiper-slide">
            <img src="https://swiperjs.com/demos/images/nature-4.jpg" alt="Slide 4" />
          </div>
        </div>
        <div className="swiper-pagination"></div>
      </div>
    </figure>
  );
};

export default Swiper;
