import React, { useEffect, useState } from "react";
import './styles/App.css';
import './styles/Slider.css';
import twitterLogo from './assets/twitter-logo.svg';
import smurfCat from './utils/smurfCat.json'
import { ethers } from "ethers";

// Constants
const TWITTER_HANDLE = 'smurfcateth';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;
const OPENSEA_LINK = 'https://testnets.opensea.io/assets/';
const TOTAL_MINT_COUNT = 50;
const CONTRACT_ADDRESS = "0x2B778f7CEa3f920242Ab6a42B828EE633b70E96D";
const MINT_AMOUNT = "1";

const App = () => {

  /*
  * Just a state variable we use to store our user's public wallet. Don't forget to import useState.
  */
  const [currentAccount, setCurrentAccount] = useState("");

  /*
  * Gotta make sure this is async.
  */
  const checkIfWalletIsConnected = async () => {
    const { ethereum } = window;

    if (!ethereum) {
        console.log("Make sure you have metamask!");
        return;
    } else {
        console.log("We have the ethereum object sir/maam", ethereum);
    }

    /*
    * Check if we're authorized to access the user's wallet
    */
    const accounts = await ethereum.request({ method: 'eth_accounts' });

    /*
    * User can have multiple authorized accounts, we grab the first one if its there!
    */
    if (accounts.length !== 0) {
      const account = accounts[0];
      console.log("Found an authorized account:", account);
      setCurrentAccount(account)

      // Setup listener! This is for the case where a user comes to our site
      // and ALREADY had their wallet connected + authorized.
      setupEventListener()
    } else {
      console.log("No authorized account found")
    }
  }

  /*
  * Implement your connectWallet method here
  */
  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }

      /*
      * Fancy method to request access to account.
      */
      const accounts = await ethereum.request({ method: "eth_requestAccounts" });

      /*
      *  This should print out public address once we authorize Metamask.
      */
      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]); 

      // Setup listener! This is for the case where a user comes to our site
      // and connected their wallet for the first time.
      setupEventListener() 
    } catch (error) {
      console.log(error)
    }
  }

  // Setup our listener.
  const setupEventListener = async () => {
    // Most of this looks the same as our function askContractToMintNft
    try {
      const { ethereum } = window;

      if (ethereum) {
        // Same stuff again
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const connectedContract = new ethers.Contract(CONTRACT_ADDRESS, smurfCat.abi, signer);

        // THIS IS THE MAGIC SAUCE.
        // This will essentially "capture" our event when our contract throws it.
        // If you're familiar with webhooks, it's very similar to that!
        connectedContract.on("newFrenMinted", (from, tokenId) => {
          console.log(from, tokenId.toNumber())
          alert(`Hey there! We've minted your NFT and sent it to your wallet. It may be blank right now. It can take a max of 10 min to show up on OpenSea. Here's the link: https://testnets.opensea.io/assets/${CONTRACT_ADDRESS}/${tokenId.toNumber()}`)
        });

        console.log("Setup event listener!")

      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error)
    }
  }

  const [isEligible, setIsEligible] = useState(false);
  const [mintCount, setMintCount] = useState(0);

  const checkEligibility = async () => {
    try {
      const response = await fetch('./utils/phase1.txt'); // Replace with the actual path
      const text = await response.text();
      const eligibleWallets = text.split('\n').map(address => address.trim().toLowerCase());

      const userWallet = currentAccount.toLowerCase();

      if (eligibleWallets.includes(userWallet)) {
        setIsEligible(true);
        setMintCount(eligibleWallets.indexOf(userWallet) < 1000 ? 2 : 1);
      } else {
        setIsEligible(false);
        setMintCount(0);
      }
    } catch (error) {
      console.log('Error reading eligible wallets:', error);
    }
  };

  useEffect(() => {
    if (currentAccount !== "") {
      checkEligibility();
    }
  }, [currentAccount]);

  const askContractToMintNft = async () => {
    try {
      if (isEligible && mintCount > 0) {
        const { ethereum } = window;

        if (ethereum) {
          const provider = new ethers.providers.Web3Provider(ethereum);
          const signer = provider.getSigner();
          const connectedContract = new ethers.Contract(CONTRACT_ADDRESS, smurfCat.abi, signer);

          console.log("Going to pop wallet now to pay gas...");
          let nftTxn = await connectedContract.mintFren(1, { gasPrice: ethers.utils.parseUnits('1', 'gwei'), gasLimit: 253578, value: ethers.utils.parseEther("0.010000") });
          let chainId = await ethereum.request({ method: 'eth_chainId' });
          console.log("Connected to chain " + chainId);

          const rinkebyChainId = "0x4";
          if (chainId !== rinkebyChainId) {
            alert("You are not connected to the Rinkeby Test Network!");
          }

          console.log("Mining...please wait.");
          await nftTxn.wait();

          // Update mint count and UI
          setMintCount(mintCount - 1);

          console.log(`Mined, see transaction: https://rinkeby.etherscan.io/tx/${nftTxn.hash}`);
        } else {
          console.log("Ethereum object doesn't exist!");
        }
      } else {
        console.log("User is not eligible to mint.");
      }
    } catch (error) {
      console.log(error);
    }
  };


  // Render Methods
  const renderNotConnectedContainer = () => (
    <button onClick={connectWallet} className="cta-button connect-wallet-button">
      Connect Wallet
    </button>
  );

  /*
  * This runs our function when the page loads.
  */
  useEffect(() => {
    checkIfWalletIsConnected();
  })

  return (
    <div className="App">
      <div className="container">
        <div className="header-container">
          <div className="title-container">
            <section className="title2">Nate Hallinan</section>
            <section className="title3">x</section>
            <section className="title">Real Smurf Cat</section>
          </div>
          <div className="slideshow">
            <div className="slides">
                <section></section>
                <section></section>
                <section></section>
                <section></section>
                <section></section>
            </div>
          </div>
          <p className="sub-text">
            Nate Hallinan's genesis NFT collection. Free to qualifying шайлушай holders.
          </p>
          {currentAccount === "" ? (
            renderNotConnectedContainer()
          ) : (
            <button
              onClick={askContractToMintNft}
              className={`cta-button connect-wallet-button ${isEligible ? 'enabled' : 'disabled'}`}
              disabled={!isEligible}
            >
              {isEligible ? `Mint NFT (${mintCount} left)` : "Not Eligible"}
            </button>
          )}
