import React, { useState } from "react";

const Eligible = () => {
  const eligibleList1 = ["0xabc...", "0xdef...", /* ... */];
  const eligibleList2 = ["0x123...", "0x456...", /* ... */];
  const eligibleList3 = ["0x789...", "0xyz...", /* ... */];

  const [userAddress, setUserAddress] = useState("");
  const [resultMessage, setResultMessage] = useState("");

  const checkEligibility = () => {
    if (eligibleList1.includes(userAddress)) {
      setResultMessage("User's address is in Eligible List 1.");
    } else if (eligibleList2.includes(userAddress)) {
      setResultMessage("User's address is in Eligible List 2.");
    } else if (eligibleList3.includes(userAddress)) {
      setResultMessage("User's address is in Eligible List 3.");
    } else {
      setResultMessage("User's address is not in any eligible list.");
    }
  };

  return (
    <div>
      <input
        type="text"
        value={userAddress}
        onChange={(e) => setUserAddress(e.target.value)}
        placeholder="Enter Ethereum address"
      />
      <button onClick={checkEligibility}>Check Eligibility</button>
      {resultMessage && <p>{resultMessage}</p>}
    </div>
  );
};

export default Eligible;
